  1281→async function buildSequence() {
  1282→    if (isOperationInProgress) return;
  1283→
  1284→    isOperationInProgress = true;
  1285→    setStatus('Building sequence...');
  1286→
  1287→    try {
  1288→        const settings = getSettings();
  1289→
  1290→        // Ensure we have sequence info
  1291→        if (!currentSequenceInfo) {
  1292→            currentSequenceInfo = await jsx.call('getActiveSequence');
  1293→        }
  1294→
  1295→        // Create cut list from selected silences
  1296→        const selectedSilences = previewSilences.filter((_, i) => selectedSilenceIndices.has(i));
  1297→
  1298→        // Calculate total duration from sequence info or last silence
  1299→        let duration = 0;
  1300→        if (currentSequenceInfo && currentSequenceInfo.duration) {
  1301→            duration = currentSequenceInfo.duration;
  1302→        } else if (previewSilences.length > 0) {
  1303→            const lastSilence = previewSilences[previewSilences.length - 1];
  1304→            duration = lastSilence.end + 10; // Add buffer
  1305→        }
  1306→
  1307→        // Call backend to generate cut list
  1308→        const cutListResponse = await fetchWithTimeout(
  1309→            `${getBackendUrl()}/cut-list`,
  1310→            {
  1311→                method: 'POST',
  1312→                headers: getAuthHeaders(),
  1313→                body: JSON.stringify({
  1314→                    sourceName: currentSequenceInfo?.name || 'Untitled Sequence',
  1315→                    sourcePath: currentSequenceInfo?.treePath || currentSequenceInfo?.name || 'Untitled',
  1316→                    duration: duration,
  1317→                    silences: selectedSilences,
  1318→                    takes: previewTakes,
  1319→                    enableTakes: settings.enableTakesDetection,
  1320→                    jCutOffset: settings.enableJCut ? settings.jcutLeadIn : 0,
  1321→                    lCutOffset: settings.enableJCut ? settings.jcutLeadOut : 0
  1322→                })
  1323→            }
  1324→        );
  1325→
  1326→        if (!cutListResponse.ok) {
  1327→            throw new Error(await parseErrorResponse(cutListResponse));
  1328→        }
  1329→
  1330→        const cutList = await cutListResponse.json();
  1331→
  1332→        // Build sequence via JSX
  1333→        const result = await jsx.call('buildSequenceFromCutList', JSON.stringify(cutList));
  1334→
  1335→        if (result && result.success) {
  1336→            setStatus(`Built sequence: ${result.sequenceName}`);
  1337→            hidePreview();
  1338→        } else {
  1339→            throw new Error(result?.error || 'Build failed');
  1340→        }
  1341→    } catch (error) {
  1342→        setStatus('Build failed: ' + error.message, true);
  1343→    } finally {
  1344→        isOperationInProgress = false;
  1345→    }
  1346→}
