  1499→/**
  1500→ * ACCESSIBILITY: Announce message to screen readers via ARIA live region
  1501→ * @param {string} message - Message to announce
  1502→ * @param {string} priority - 'polite' or 'assertive'
  1503→ */
  1504→function announceToScreenReader(message, priority = 'polite') {
  1505→    // Use existing status element or create temporary one
  1506→    const statusEl = document.getElementById('status');
  1507→    if (statusEl && priority === 'polite') {
  1508→        const originalText = statusEl.textContent;
  1509→        statusEl.textContent = message;
  1510→        // Restore after announcement
  1511→        setTimeout(() => {
  1512→            if (statusEl.textContent === message) {
  1513→                statusEl.textContent = originalText;
  1514→            }
  1515→        }, 1000);
  1516→        return;
  1517→    }
  1518→
  1519→    // Create temporary live region for assertive announcements
  1520→    const liveRegion = document.createElement('div');
  1521→    liveRegion.setAttribute('role', 'alert');
  1522→    liveRegion.setAttribute('aria-live', priority);
  1523→    liveRegion.setAttribute('aria-atomic', 'true');
  1524→    liveRegion.className = 'sr-only';
  1525→    liveRegion.style.cssText = 'position:absolute;width:1px;height:1px;padding:0;margin:-1px;overflow:hidden;clip:rect(0,0,0,0);border:0;';
  1526→    liveRegion.textContent = message;
  1527→    document.body.appendChild(liveRegion);
  1528→
  1529→    // Remove after announcement
  1530→    setTimeout(() => liveRegion.remove(), 1000);
  1531→}
