  1611→ */
  1612→async function lookupLicense() {
  1613→    const lookupEmailInput = document.getElementById('lookupEmailInput');
  1614→    const licenseKeyInput = document.getElementById('licenseKeyInput');
  1615→
  1616→    if (!lookupEmailInput) return;
  1617→
  1618→    const email = lookupEmailInput.value.trim().toLowerCase();
