  1620→        setStatus('Please enter your email address', true);
  1621→        return;
  1622→    }
  1623→
  1624→    // Basic email validation
  1625→    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  1626→    if (!emailRegex.test(email)) {
  1627→        setStatus('Please enter a valid email address', true);
  1628→        return;
  1629→    }
  1630→
  1631→    const lookupBtn = document.getElementById('lookupLicenseBtn');
