  1770→
  1771→// ============================================================================
  1772→// DIAGNOSTICS
  1773→// ============================================================================
  1774→let diagnosticLogs = [];
  1775→
  1776→async function runDiagnostics() {
  1777→    if (!ui.debugLogs || !ui.debugQuickDiagnosis) return;
  1778→
  1779→    diagnosticLogs = [];
