  1795→            addDiagnosticLog('✓ Basic ExtendScript works: 1+1 = ' + basicResult);
  1796→        } catch (e) {
  1797→            addDiagnosticLog('✗ Basic ExtendScript FAILED: ' + e.message);
  1798→            addDiagnosticLog('  → This means Premiere Pro cannot run ANY ExtendScript');
  1799→            addDiagnosticLog('  → Make sure CEP debug mode is enabled and Premiere was restarted');
  1800→        }
  1801→
  1802→        // Test 0.6: Check if hostScript.jsx functions are loaded
  1803→        addDiagnosticLog('Test 0.6: Checking if hostScript.jsx is loaded...');
  1804→        try {
  1805→            const typeofGetVersion = await jsx.evalScript('typeof getVersion');
  1806→            if (typeofGetVersion === 'function') {
