  1824→
  1825→        // Test 2: Check for active sequence
  1826→        addDiagnosticLog('Test 2: Checking for active sequence...');
  1827→        try {
  1828→            const hasSequence = await checkSequenceOpen(0, 0); // No retries for diagnostics
  1829→            addDiagnosticLog('✓ checkSequenceOpen() returned: ' + hasSequence);
  1830→        } catch (e) {
  1831→            addDiagnosticLog('✗ checkSequenceOpen() FAILED: ' + e.message);
  1832→        }
  1833→
  1834→        // Test 3: Get sequence info
  1835→        addDiagnosticLog('Test 3: Getting sequence info...');
  1836→        try {
  1837→            const seqInfo = await jsx.call('getActiveSequence');
  1838→            if (seqInfo && !seqInfo.error) {
  1839→                addDiagnosticLog('✓ Sequence found: ' + (seqInfo.name || 'Unknown'));
  1840→                addDiagnosticLog('  - Sequence ID: ' + (seqInfo.sequenceID || 'N/A'));
  1841→                addDiagnosticLog('  - Duration: ' + (seqInfo.duration || 'N/A'));
  1842→                addDiagnosticLog('  - Frame size: ' + (seqInfo.frameSizeHorizontal || 'N/A') + 'x' + (seqInfo.frameSizeVertical || 'N/A'));
  1843→            } else {
