  1849→
  1850→        // Test 4: Check project sequences
  1851→        addDiagnosticLog('Test 4: Checking all project sequences...');
  1852→        try {
  1853→            const sequences = await jsx.call('getAllSequences');
  1854→            if (sequences && sequences.length > 0) {
  1855→                addDiagnosticLog('✓ Total sequences in project: ' + sequences.length);
  1856→                sequences.forEach((seq, i) => {
