  1865→
  1866→        // Test 5: Check for active sequence via DOM
  1867→        addDiagnosticLog('Test 5: Checking app.project.activeSequence...');
  1868→        try {
  1869→            const result = await jsx.evalScript(`
  1870→                (function() {
  1871→                    try {
  1872→                        if (app && app.project && app.project.activeSequence) {
  1873→                            return JSON.stringify({
  1874→                                success: true,
