  1888→            `);
  1889→            const data = JSON.parse(result);
  1890→            if (data.success) {
  1891→                addDiagnosticLog('✓ app.project.activeSequence found: ' + data.name + ' (ID: ' + data.id + ')');
  1892→            } else {
