  1897→        }
  1898→
  1899→        // Test 6: Check QE DOM availability
  1900→        addDiagnosticLog('Test 6: Checking QE DOM availability...');
  1901→        try {
  1902→            const qeResult = await jsx.evalScript(`
  1903→                (function() {
  1904→                    try {
  1905→                        if (typeof qe !== 'undefined' && qe !== null) {
  1906→                            return JSON.stringify({ available: true });
  1907→                        } else {
