  1934→    }
  1935→}
  1936→
  1937→function addDiagnosticLog(message) {
  1938→    diagnosticLogs.push(message);
  1939→    console.log('[SPLICE DEBUG] ' + message);
  1940→}
  1941→
  1942→function displayDiagnosticLogs() {
  1943→    if (!ui.debugLogs) return;
