  1963→    } else if (logs.includes('checkSequenceOpen() returned: false') && logs.includes('No active sequence found')) {
  1964→        diagnosis = '❌ No Active Sequence';
  1965→        solution = 'No sequence is active in the Timeline. Double-click a sequence in the Project panel to open it in the Timeline.';
  1966→    } else if (logs.includes('app.project.activeSequence NOT found') && logs.includes('Sequence found')) {
  1967→        diagnosis = '❌ Sequence Not Active';
  1968→        solution = 'A sequence exists but is not the active sequence. Click on the sequence tab in the Timeline to make it active.';
  1969→    } else if (logs.includes('checkSequenceOpen() returned: true') && logs.includes('app.project.activeSequence found')) {
  1970→        diagnosis = '✅ Everything Looks Good';
  1971→        solution = 'The plugin is working correctly. The sequence is detected and active. Try pressing GO again.';
  1972→    } else if (logs.includes('No sequences found in project')) {
  1973→        diagnosis = '❌ No Sequences in Project';
  1974→        solution = 'Create a sequence in Premiere Pro first (File > New > Sequence), then open it in the Timeline.';
  1975→    } else {
