  1977→        solution = 'Copy the diagnostic logs below and send them to support for further assistance.';
  1978→    }
  1979→
  1980→    ui.debugQuickDiagnosis.innerHTML = `
  1981→        <div class="diagnosis-result">
  1982→            <div class="diagnosis-title">${diagnosis}</div>
  1983→            <div class="diagnosis-solution">${solution}</div>
