  1174→/**
  1175→ * Look up license key by email address
  1176→ */
  1177→async function lookupLicense() {
  1178→    const lookupEmailInput = document.getElementById('lookupEmailInput');
  1179→    const licenseKeyInput = document.getElementById('licenseKeyInput');
  1180→
  1181→    if (!lookupEmailInput) return;
  1182→
  1183→    const email = lookupEmailInput.value.trim().toLowerCase();
  1184→    if (!email) {
  1185→        setStatus('Please enter your email address', true);
  1186→        return;
  1187→    }
  1188→
  1189→    // Basic email validation
  1190→    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  1191→    if (!emailRegex.test(email)) {
  1192→        setStatus('Please enter a valid email address', true);
  1193→        return;
  1194→    }
  1195→
  1196→    const lookupBtn = document.getElementById('lookupLicenseBtn');
  1197→    if (lookupBtn) {
  1198→        lookupBtn.disabled = true;
  1199→        lookupBtn.textContent = 'Looking up...';
  1200→    }
  1201→
  1202→    try {
  1203→        const response = await fetchWithTimeout(
  1204→            `${getBackendUrl()}/license/lookup`,
  1205→            {
  1206→                method: 'POST',
  1207→                headers: { 'Content-Type': 'application/json' },
  1208→                body: JSON.stringify({ email })
  1209→            },
  1210→            30000
  1211→        );
  1212→
  1213→        const result = await response.json();
  1214→
  1215→        // Note: For security, the backend does NOT return the license key directly.
  1216→        // It sends the key via email to prevent enumeration attacks.
  1217→        if (result.success) {
  1218→            setStatus(result.message || 'If a license exists, it has been sent to your email.');
  1219→        } else {
  1220→            setStatus(result.error || 'Lookup failed. Please try again.', true);
  1221→        }
  1222→    } catch (error) {
  1223→        setStatus('Lookup failed: ' + error.message, true);
  1224→    } finally {
  1225→        if (lookupBtn) {
  1226→            lookupBtn.disabled = false;
  1227→            lookupBtn.textContent = 'Lookup';
  1228→        }
  1229→    }
  1230→}
