   449→                wideShotPercentage: settings.wideShotPercentage,
   450→                wideShotTracks: settings.wideShotTracks,
   451→                minShotDuration: settings.minShotDuration,
   452→                switchingFrequency: settings.switchingFrequency
   453→            })
   454→        }, 120000);
   455→
   456→        if (!response.ok) {
   457→            const errorMsg = await parseErrorResponse(response);
   458→            throw new Error(errorMsg);
   459→        }
   460→
   461→        const data = await response.json();
   462→        analysisResults = data;
   463→
   464→        // Display results
   465→        displayMultitrackResults(data);
   466→
   467→        // Enable apply button
   468→        if (multitrackUI.applyBtn) {
   469→            multitrackUI.applyBtn.disabled = false;
   470→        }
   471→
   472→        const errorInfo = data.finalError ? ` (${data.finalError.toFixed(1)}% deviation)` : '';
   473→        setStatus(`Auto-balance complete${errorInfo}`);
   474→
   475→    } catch (err) {
   476→        console.error('[SPLICE Multitrack] Auto-balance error:', err);
   477→        setStatus('Auto-balance failed: ' + err.message, true);
   478→    } finally {
   479→        isMultitrackOperationInProgress = false;
   480→        if (multitrackUI.autoBalanceBtn) {
   481→            multitrackUI.autoBalanceBtn.disabled = false;
   482→            multitrackUI.autoBalanceBtn.textContent = 'Auto-Balance';
   483→        }
   484→    }
   485→}
   486→
   487→// ============================================================================
   488→// DISPLAY RESULTS
   489→// ============================================================================
   490→function displayMultitrackResults(results) {
   491→    if (!results) return;
   492→
   493→    // Show preview section
   494→    if (multitrackUI.preview) {
   495→        multitrackUI.preview.classList.remove('hidden');
   496→    }
   497→
   498→    // Render distribution chart
   499→    renderDistributionChart(results.metadata);
   500→
   501→    // Render speaker stats
   502→    renderSpeakerStats(results.metadata);
   503→
   504→    // Render decision list
   505→    renderDecisionList(results.decisions || []);
   506→}
   507→
   508→function renderDistributionChart(metadata) {
   509→    if (!multitrackUI.distributionChart || !metadata) return;
   510→
   511→    const speakerPercentages = metadata.speakerPercentages || {};
   512→    const wideShotPct = metadata.wideShotPercentage || 0;
   513→
   514→    const fragment = document.createDocumentFragment();
   515→
   516→    // Add speaker bars
   517→    let speakerIndex = 0;
   518→    for (const [speaker, pct] of Object.entries(speakerPercentages)) {
   519→        if (pct > 0) {
   520→            const bar = document.createElement('div');
   521→            bar.className = `distribution-bar speaker-${speakerIndex % 4}`;
   522→            bar.style.width = `${pct}%`;
   523→            bar.textContent = pct >= 10 ? `${speaker}: ${pct.toFixed(0)}%` : '';
   524→            bar.title = `${speaker}: ${pct.toFixed(1)}%`;
   525→            fragment.appendChild(bar);
   526→        }
   527→        speakerIndex++;
   528→    }
   529→
   530→    // Add wide shot bar
   531→    if (wideShotPct > 0) {
   532→        const bar = document.createElement('div');
   533→        bar.className = 'distribution-bar wide-shot';
   534→        bar.style.width = `${wideShotPct}%`;
   535→        bar.textContent = wideShotPct >= 8 ? `Wide: ${wideShotPct.toFixed(0)}%` : '';
   536→        bar.title = `Wide Shot: ${wideShotPct.toFixed(1)}%`;
   537→        fragment.appendChild(bar);
   538→    }
   539→
   540→    multitrackUI.distributionChart.innerHTML = '';
   541→    multitrackUI.distributionChart.appendChild(fragment);
   542→}
   543→
   544→function renderSpeakerStats(metadata) {
   545→    if (!multitrackUI.speakerStats || !metadata) return;
   546→
   547→    const speakerPercentages = metadata.speakerPercentages || {};
   548→    const wideShotPct = metadata.wideShotPercentage || 0;
   549→
   550→    const fragment = document.createDocumentFragment();
   551→
   552→    // Add speaker stats
   553→    // SECURITY: Escape speaker names to prevent XSS
   554→    let speakerIndex = 0;
   555→    for (const [speaker, pct] of Object.entries(speakerPercentages)) {
   556→        const stat = document.createElement('div');
   557→        stat.className = 'speaker-stat';
   558→        stat.innerHTML = `
   559→            <span class="speaker-stat-dot speaker-${speakerIndex % 4}"></span>
   560→            <span>${escapeHtml(speaker)}: ${escapeHtml(pct.toFixed(1))}%</span>
   561→        `;
   562→        fragment.appendChild(stat);
   563→        speakerIndex++;
   564→    }
   565→
   566→    // Add wide shot stat
   567→    if (wideShotPct > 0) {
   568→        const stat = document.createElement('div');
   569→        stat.className = 'speaker-stat';
   570→        stat.innerHTML = `
   571→            <span class="speaker-stat-dot wide-shot"></span>
   572→            <span>Wide: ${escapeHtml(wideShotPct.toFixed(1))}%</span>
   573→        `;
   574→        fragment.appendChild(stat);
   575→    }
   576→
   577→    // Add avg shot duration
   578→    if (metadata.averageShotDuration) {
   579→        const stat = document.createElement('div');
   580→        stat.className = 'speaker-stat';
   581→        stat.innerHTML = `<span style="color: #888;">Avg: ${escapeHtml(metadata.averageShotDuration.toFixed(1))}s</span>`;
   582→        fragment.appendChild(stat);
   583→    }
   584→
   585→    multitrackUI.speakerStats.innerHTML = '';
   586→    multitrackUI.speakerStats.appendChild(fragment);
   587→}
   588→
   589→function renderDecisionList(decisions) {
   590→    if (!multitrackUI.decisionList) return;
   591→
   592→    // Update count
   593→    if (multitrackUI.decisionCount) {
   594→        multitrackUI.decisionCount.textContent = decisions.length;
   595→    }
   596→
   597→    const fragment = document.createDocumentFragment();
   598→
   599→    // Limit to first 50 for performance
   600→    const displayDecisions = decisions.slice(0, 50);
   601→
   602→    displayDecisions.forEach((decision, i) => {
   603→        const item = document.createElement('div');
   604→        item.className = 'decision-item';
   605→
   606→        const speakerClass = decision.isWideShot ? 'wide-shot' : '';
   607→        const startTime = formatTime(decision.startTime);
   608→        const endTime = formatTime(decision.endTime);
   609→
   610→        // SECURITY: Escape dynamic content to prevent XSS
   611→        item.innerHTML = `
   612→            <span class="decision-time">${escapeHtml(startTime)}-${escapeHtml(endTime)}</span>
   613→            <span class="decision-speaker ${speakerClass}">${escapeHtml(decision.speakerName)}</span>
   614→            <span class="decision-reason">${escapeHtml(decision.reason || '')}</span>
   615→            <button class="decision-seek" data-time="${escapeHtml(decision.startTime)}" title="Seek to ${escapeHtml(startTime)}" aria-label="Seek to ${escapeHtml(startTime)}">&gt;</button>
   616→        `;
   617→
   618→        fragment.appendChild(item);
   619→    });
   620→
   621→    if (decisions.length > 50) {
   622→        const more = document.createElement('div');
   623→        more.className = 'decision-item';
   624→        more.style.justifyContent = 'center';
   625→        more.style.color = '#888';
   626→        more.textContent = `+ ${decisions.length - 50} more decisions`;
   627→        fragment.appendChild(more);
   628→    }
   629→
   630→    multitrackUI.decisionList.innerHTML = '';
   631→    multitrackUI.decisionList.appendChild(fragment);
   632→}
   633→
   634→function handleDecisionListClick(e) {
   635→    const seekBtn = e.target.closest('.decision-seek');
   636→    if (seekBtn) {
   637→        e.stopPropagation();
   638→        const time = parseFloat(seekBtn.dataset.time);
   639→        if (!isNaN(time)) {
   640→            seekToTime(time);
   641→        }
   642→    }
   643→}
   644→
   645→// ============================================================================
   646→// SEEK TO TIME (CEP VERSION)
   647→// ============================================================================
   648→async function seekToTime(seconds) {
   649→    try {
   650→        await jsx.call('seekToTime', seconds);
   651→    } catch (e) {
   652→        console.warn('[SPLICE Multitrack] Failed to seek:', e);
   653→    }
   654→}
   655→
   656→// ============================================================================
   657→// APPLY MULTITRACK CUTS
   658→// ============================================================================
   659→async function applyMultitrackCuts() {
   660→    if (!analysisResults || !analysisResults.decisions) {
   661→        setStatus('No analysis data. Run analysis first.');
   662→        return;
   663→    }
   664→
   665→    if (isMultitrackOperationInProgress) return;
   666→
   667→    isMultitrackOperationInProgress = true;
   668→    if (multitrackUI.applyBtn) {
   669→        multitrackUI.applyBtn.disabled = true;
   670→        multitrackUI.applyBtn.textContent = 'Applying...';
   671→    }
   672→
   673→    try {
   674→        setStatus('Building multitrack sequence...');
   675→
   676→        // Check if builder is available
   677→        if (!window.spliceBuilder) {
   678→            throw new Error('Builder not initialized');
   679→        }
   680→
   681→        // Get active sequence info
   682→        const seqInfo = await jsx.call('getActiveSequence');
   683→        if (!seqInfo) {
   684→            throw new Error('No active sequence');
   685→        }
   686→
   687→        // Create cut list from decisions
   688→        const cutList = {
   689→            version: '3.5',
   690→            source: {
   691→                name: seqInfo.name,
   692→                path: seqInfo.treePath || seqInfo.name
   693→            },
   694→            segments: analysisResults.decisions.map(d => ({
   695→                type: d.isWideShot ? 'wide_shot' : (d.speakerIndex >= 0 ? 'speaker' : 'speech'),
   696→                sourceName: seqInfo.name,
   697→                sourcePath: seqInfo.treePath || seqInfo.name,
   698→                inPoint: d.startTime,
   699→                outPoint: d.endTime,
   700→                speaker: d.speakerName,
   701→                videoTrack: d.videoTrack,
   702→                colorHint: d.isWideShot ? 'yellow' :
   703→                           (d.speakerIndex === 0 ? 'mango' :
   704→                            d.speakerIndex === 1 ? 'caribbean' :
   705→                            d.speakerIndex === 2 ? 'lavender' : 'cerulean')
   706→            })),
   707→            metadata: {
   708→                isMultitrack: true,
   709→                speakerCount: speakerConfig.length,
   710→                decisionCount: analysisResults.decisions.length,
   711→                ...analysisResults.metadata
   712→            }
   713→        };
   714→
   715→        // Build sequence
   716→        const result = await window.spliceBuilder.buildSequenceFromCutList(cutList, {
   717→            suffix: '_MULTITRACK'
   718→        });
   719→
   720→        if (result.success) {
   721→            // Hide preview
   722→            if (multitrackUI.preview) {
   723→                multitrackUI.preview.classList.add('hidden');
   724→            }
   725→
   726→            setStatus(`Multitrack sequence created: ${result.stats?.clipsInserted || 0} clips`);
   727→
   728→            // Clear results
   729→            analysisResults = null;
   730→        } else {
   731→            throw new Error(result.error || 'Failed to build sequence');
   732→        }
   733→
   734→    } catch (err) {
   735→        console.error('[SPLICE Multitrack] Apply error:', err);
   736→        setStatus('Apply failed: ' + err.message, true);
   737→    } finally {
   738→        isMultitrackOperationInProgress = false;
   739→        if (multitrackUI.applyBtn) {
   740→            multitrackUI.applyBtn.disabled = true;
   741→            multitrackUI.applyBtn.textContent = 'Apply';
   742→        }
   743→    }
   744→}
   745→
   746→// ============================================================================
   747→// EXPORTS
   748→// ============================================================================
   749→// Expose for initialization from main.js
   750→if (typeof window !== 'undefined') {
   751→    window.initMultitrackUI = initMultitrackUI;
   752→    window.spliceMultitrack = {
   753→        init: initMultitrackUI,
   754→        analyze: analyzeMultitrack,
   755→        autoBalance: autoBalanceMultitrack,
   756→        apply: applyMultitrackCuts,
   757→        getSettings: getMultitrackSettings,
   758→        getResults: () => analysisResults
   759→    };
   760→}
