  1681→function formatDuration(seconds) {
  1682→  if (!seconds) return '0:00';
  1683→  const mins = Math.floor(seconds / 60);
  1684→  const secs = Math.round(seconds % 60);
  1685→  return `${mins}:${secs.toString().padStart(2, '0')}`;
  1686→}
