/**
 * Verify Beta Tester Accounts Script
 * 
 * Checks if beta tester accounts exist in the database and are properly configured
 */

const { Pool } = require('pg');
require('dotenv').config();

const pool = new Pool({
  connectionString: process.env.DATABASE_URL,
  ssl: { rejectUnauthorized: false },
  max: 5,
  idleTimeoutMillis: 30000,
  connectionTimeoutMillis: 5000
});

// Expected beta tester license keys (newly created)
const expectedBetaTesters = [
  {
    name: 'Josh Irizarry',
    licenseKey: 'SPLICE-GW6N-88DP-5DDD',
    customerId: 'beta_josh_irizarry'
  },
  {
    name: 'Danny Isakov',
    licenseKey: 'SPLICE-Y2Q9-6G9G-MFQE',
    customerId: 'beta_danny_isakov'
  },
  {
    name: 'Jimmy Nick',
    licenseKey: 'SPLICE-UC2G-R5H8-UKHF',
    customerId: 'beta_jimmy_nick'
  }
