   148â†’async function createLicenseKey(client, stripeCustomerId) {
   149â†’  // Check if customer already has a key
   150â†’  const existing = await client.query(
   151â†’    'SELECT key FROM license_keys WHERE stripe_customer_id = $1',
   152â†’    [stripeCustomerId]
   153â†’  );
   154â†’  
   155â†’  if (existing.rows.length > 0) {
   156â†’    return { key: existing.rows[0].key, existing: true };
   157â†’  }
   158â†’  
   159â†’  // Generate unique key with retry logic
   160â†’  let key;
   161â†’  let attempts = 0;
   162â†’  const maxAttempts = 10;
   163â†’  
   164â†’  while (attempts < maxAttempts) {
   165â†’    key = generateKeyString();
   166â†’    try {
   167â†’      await client.query(
   168â†’        `INSERT INTO license_keys (key, stripe_customer_id)
   169â†’         VALUES ($1, $2)`,
   170â†’        [key, stripeCustomerId]
   171â†’      );
   172â†’      break;
   173â†’    } catch (err) {
   174â†’      if (err.code === '23505') { // Unique violation
   175â†’        attempts++;
   176â†’        continue;
   177â†’      }
   178â†’      throw err;
   179â†’    }
   180â†’  }
   181â†’  
   182â†’  if (attempts >= maxAttempts) {
   183â†’    throw new Error('Failed to generate unique license key');
   184â†’  }
   185â†’  
   186â†’  return { key, existing: false };
   187â†’}
