document.addEventListener('DOMContentLoaded', () => {
    jsx.init();
});

// Export for modules
window.SPLICE_CONFIG = SPLICE_CONFIG;
window.getBackendUrl = getBackendUrl;
window.fetchWithTimeout = fetchWithTimeout;
