  1150→function clearSequence() {
  1151→    var seq = app.project.activeSequence;
  1152→    if (!seq) return false;
  1153→
  1154→    // Remove all video clips
  1155→    for (var t = 0; t < seq.videoTracks.numTracks; t++) {
