   753→
   754→            if (e.target.classList.contains('preview-item-check')) {
   755→                // Checkbox clicked
   756→                toggleSilenceSelection(index);
   757→            } else {
   758→                // Item clicked - seek to time
   759→                const data = type === 'silence' ? previewSilences[index] : previewTakes[index];
