   869→async function applyPreviewAndMarkers() {
   870→    if (isOperationInProgress) return;
   871→
   872→    isOperationInProgress = true;
   873→    setStatus('Adding markers...');
   874→
   875→    try {
   876→        // Add silence markers
   877→        const selectedSilences = previewSilences.filter((_, i) => selectedSilenceIndices.has(i));
   878→        for (const silence of selectedSilences) {
   879→            await jsx.call('createMarker', silence.start, 'SPLICE: Silence', silence.end - silence.start, null, 1);
   880→        }
   881→
   882→        // Add take markers
   883→        for (const take of previewTakes) {
   884→            await jsx.call('createMarker', take.start, take.label || `Take ${take.takeNumber}`, take.end - take.start, null, 5);
   885→        }
   886→
   887→        setStatus(`Added ${selectedSilences.length + previewTakes.length} markers`);
   888→    } catch (error) {
   889→        setStatus('Failed to add markers: ' + error.message, true);
   890→    } finally {
   891→        isOperationInProgress = false;
   892→    }
   893→}
