   905→async function buildSequence() {
   906→    if (isOperationInProgress) return;
   907→
   908→    isOperationInProgress = true;
   909→    setStatus('Building sequence...');
   910→
   911→    try {
   912→        const settings = getSettings();
   913→
   914→        // Ensure we have sequence info
   915→        if (!currentSequenceInfo) {
   916→            currentSequenceInfo = await jsx.call('getActiveSequence');
   917→        }
   918→
   919→        // Create cut list from selected silences
   920→        const selectedSilences = previewSilences.filter((_, i) => selectedSilenceIndices.has(i));
   921→
   922→        // Calculate total duration from sequence info or last silence
   923→        let duration = 0;
   924→        if (currentSequenceInfo && currentSequenceInfo.duration) {
   925→            duration = currentSequenceInfo.duration;
   926→        } else if (previewSilences.length > 0) {
   927→            const lastSilence = previewSilences[previewSilences.length - 1];
   928→            duration = lastSilence.end + 10; // Add buffer
   929→        }
   930→
   931→        // Call backend to generate cut list
   932→        const cutListResponse = await fetchWithTimeout(
   933→            `${getBackendUrl()}/cut-list`,
   934→            {
   935→                method: 'POST',
   936→                headers: getAuthHeaders(),
   937→                body: JSON.stringify({
   938→                    sourceName: currentSequenceInfo?.name || 'Untitled Sequence',
   939→                    sourcePath: currentSequenceInfo?.treePath || currentSequenceInfo?.name || 'Untitled',
   940→                    duration: duration,
   941→                    silences: selectedSilences,
   942→                    takes: previewTakes,
   943→                    enableTakes: settings.enableTakesDetection,
   944→                    jCutOffset: settings.enableJCut ? settings.jcutLeadIn : 0,
   945→                    lCutOffset: settings.enableJCut ? settings.jcutLeadOut : 0
   946→                })
   947→            }
   948→        );
   949→
   950→        if (!cutListResponse.ok) {
   951→            throw new Error(await parseErrorResponse(cutListResponse));
   952→        }
   953→
   954→        const cutList = await cutListResponse.json();
   955→
   956→        // Build sequence via JSX
   957→        const result = await jsx.call('buildSequenceFromCutList', JSON.stringify(cutList));
   958→
   959→        if (result && result.success) {
   960→            setStatus(`Built sequence: ${result.sequenceName}`);
   961→            hidePreview();
   962→        } else {
   963→            throw new Error(result?.error || 'Build failed');
   964→        }
   965→    } catch (error) {
   966→        setStatus('Build failed: ' + error.message, true);
   967→    } finally {
   968→        isOperationInProgress = false;
   969→    }
   970→}
