   352→    let user = result.rows[0];
   353→
   354→    // If actual usage was less than reserved, refund the difference
   355→    if (difference > 0) {
   356→      const updateResult = await client.query(
   357→        `UPDATE users
   358→         SET hours_remaining = hours_remaining + $1,
   359→             updated_at = CURRENT_TIMESTAMP
   360→         WHERE stripe_customer_id = $2
   361→         RETURNING hours_remaining, hours_total, isolation_hours_remaining, isolation_hours_total, tier`,
   362→        [difference, stripeCustomerId]
   363→      );
   364→      user = { ...user, ...updateResult.rows[0] };
   365→
   366→      // Log the adjustment
   367→      await client.query(
   368→        `INSERT INTO usage_log (user_id, stripe_customer_id, audio_duration_seconds, hours_used, endpoint)
   369→         VALUES ($1, $2, $3, $4, $5)`,
   370→        [user.id, stripeCustomerId, -(reservedSeconds - actualSeconds), -difference, `${endpoint}:adjusted`]
   371→      );
   372→    } else if (difference < 0) {
   373→      // Actual usage was more than reserved - deduct the extra
   374→      const updateResult = await client.query(
   375→        `UPDATE users
   376→         SET hours_remaining = GREATEST(0, hours_remaining - $1),
   377→             updated_at = CURRENT_TIMESTAMP
   378→         WHERE stripe_customer_id = $2
   379→         RETURNING hours_remaining, hours_total, isolation_hours_remaining, isolation_hours_total, tier`,
