   382→      user = { ...user, ...updateResult.rows[0] };
   383→
   384→      // Log the additional usage
   385→      await client.query(
   386→        `INSERT INTO usage_log (user_id, stripe_customer_id, audio_duration_seconds, hours_used, endpoint)
   387→         VALUES ($1, $2, $3, $4, $5)`,
   388→        [user.id, stripeCustomerId, actualSeconds - reservedSeconds, -difference, `${endpoint}:additional`]
   389→      );
   390→    }
   391→
   392→    await client.query('COMMIT');
   393→
   394→    console.log(`[UsageTracking] Confirmed ${actualHours.toFixed(4)}hrs (reserved: ${reservedHours.toFixed(4)}) for ${stripeCustomerId}`);
   395→
   396→    // Build balance from data we already have (no extra SELECT needed)
   397→    const hoursTotal = parseFloat(user.hours_total);
   398→    const hoursRemaining = parseFloat(user.hours_remaining);
   399→    const isolationHoursTotal = parseFloat(user.isolation_hours_total || 0);
