   405→      team: 'Team',
   406→      cancelled: 'Cancelled'
   407→    };
   408→
   409→    return {
   410→      hoursRemaining,
   411→      hoursTotal,
   412→      isolationHoursRemaining,
   413→      isolationHoursTotal,
   414→      tier: user.tier,
   415→      tierName: tierNames[user.tier] || user.tier,
   416→      percentUsed: hoursTotal > 0 ? ((hoursTotal - hoursRemaining) / hoursTotal * 100).toFixed(1) : '0.0',
   417→      isolationPercentUsed: isolationHoursTotal > 0 ? ((isolationHoursTotal - isolationHoursRemaining) / isolationHoursTotal * 100).toFixed(1) : '0.0',
   418→      hasIsolationAccess: TIER_ISOLATION_HOURS[user.tier] > 0,
   419→      isolationOverageRate: ISOLATION_OVERAGE_RATE
   420→    };
   421→  } catch (err) {
