   671→/**
   672→ * Deduct usage from user's balance
   673→ * Optimized: uses RETURNING to avoid extra SELECT after UPDATE
   674→ * Uses transaction with rollback on failure for atomic UPDATE + INSERT
   675→ * Reports overage to Stripe for billing when user exceeds included hours
   676→ */
   677→async function deductUsage(stripeCustomerId, audioDurationSeconds, endpoint = 'unknown') {
   678→  const hoursUsed = audioDurationSeconds / 3600;
   679→
   680→  const client = await pool.connect();
   681→  try {
   682→    await client.query('BEGIN');
   683→
   684→    // First get current balance to calculate overage
   685→    const currentResult = await client.query(
   686→      `SELECT id, hours_remaining, hours_total, isolation_hours_remaining, isolation_hours_total, tier
   687→       FROM users WHERE stripe_customer_id = $1 FOR UPDATE`,
   688→      [stripeCustomerId]
   689→    );
   690→
   691→    if (currentResult.rows.length === 0) {
   692→      await client.query('ROLLBACK');
   693→      throw new Error('User not found');
   694→    }
   695→
   696→    const currentUser = currentResult.rows[0];
   697→    const currentRemaining = parseFloat(currentUser.hours_remaining);
   698→
   699→    // Calculate overage (hours used beyond what's remaining)
   700→    let overageHours = 0;
   701→    if (hoursUsed > currentRemaining) {
   702→      overageHours = hoursUsed - currentRemaining;
   703→    }
   704→
   705→    // Update balance (clamp at 0)
   706→    const updateResult = await client.query(
   707→      `UPDATE users
   708→       SET hours_remaining = GREATEST(0, hours_remaining - $1),
   709→           updated_at = CURRENT_TIMESTAMP
   710→       WHERE stripe_customer_id = $2
   711→       RETURNING id, hours_remaining, hours_total, isolation_hours_remaining, isolation_hours_total, tier`,
   712→      [hoursUsed, stripeCustomerId]
   713→    );
   714→
   715→    const user = updateResult.rows[0];
   716→
   717→    // Log usage
   718→    await client.query(
   719→      `INSERT INTO usage_log (user_id, stripe_customer_id, audio_duration_seconds, hours_used, endpoint)
   720→       VALUES ($1, $2, $3, $4, $5)`,
   721→      [user.id, stripeCustomerId, audioDurationSeconds, hoursUsed, endpoint]
   722→    );
   723→
   724→    await client.query('COMMIT');
   725→
   726→    // Report overage to Stripe if any (after commit to not block transaction)
   727→    let overageCharge = 0;
   728→    if (overageHours > 0) {
   729→      overageCharge = overageHours * HOURS_OVERAGE_RATE;
   730→      const usageReport = await reportHoursOverage(stripeCustomerId, overageHours);
   731→      if (usageReport.success) {
   732→        console.log(`[SPLICE] Hours overage reported to Stripe: ${stripeCustomerId} - ${overageHours.toFixed(2)}hrs ($${overageCharge.toFixed(2)})`);
   733→      } else {
   734→        console.warn(`[SPLICE] Hours overage billing failed: ${usageReport.error}`);
   735→      }
   736→    }
   737→
   738→    // Build balance from RETURNING data (no extra SELECT needed)
   739→    const hoursTotal = parseFloat(user.hours_total);
   740→    const hoursRemaining = parseFloat(user.hours_remaining);
   741→    const isolationHoursTotal = parseFloat(user.isolation_hours_total || 0);
   742→    const isolationHoursRemaining = parseFloat(user.isolation_hours_remaining || 0);
   743→
   744→    const tierNames = {
   745→      starter: 'Starter',
   746→      pro: 'Pro',
   747→      team: 'Team',
   748→      cancelled: 'Cancelled'
   749→    };
   750→
   751→    return {
   752→      hoursRemaining,
   753→      hoursTotal,
   754→      isolationHoursRemaining,
   755→      isolationHoursTotal,
   756→      tier: user.tier,
   757→      tierName: tierNames[user.tier] || user.tier,
   758→      percentUsed: hoursTotal > 0 ? ((hoursTotal - hoursRemaining) / hoursTotal * 100).toFixed(1) : '0.0',
   759→      isolationPercentUsed: isolationHoursTotal > 0 ? ((isolationHoursTotal - isolationHoursRemaining) / isolationHoursTotal * 100).toFixed(1) : '0.0',
   760→      hasIsolationAccess: TIER_ISOLATION_HOURS[user.tier] > 0,
   761→      isolationOverageRate: ISOLATION_OVERAGE_RATE,
   762→      overageHours,
   763→      overageCharge
   764→    };
   765→  } catch (err) {
   766→    await client.query('ROLLBACK');
   767→    throw err;
   768→  } finally {
   769→    client.release();
   770→  }
   771→}
