   968→/**
   969→ * Deduct one music credit from customer
   970→ * @param {string} stripeCustomerId - Stripe customer ID
   971→ * @returns {Promise<{success: boolean, remaining: number, charged: number}>}
   972→ */
   973→async function deductMusicCredit(stripeCustomerId) {
   974→  const client = await pool.connect();
   975→  try {
   976→    await client.query('BEGIN');
   977→
   978→    // Lock row for update
   979→    const result = await client.query(
   980→      `SELECT music_credits_remaining, tier
   981→       FROM users
   982→       WHERE stripe_customer_id = $1
   983→       FOR UPDATE`,
   984→      [stripeCustomerId]
   985→    );
   986→
   987→    if (result.rows.length === 0) {
   988→      await client.query('ROLLBACK');
   989→      return { success: false, remaining: 0, charged: 0, error: 'User not found' };
   990→    }
   991→
   992→    const { music_credits_remaining } = result.rows[0];
   993→    let charged = 0;
   994→
   995→    if (music_credits_remaining > 0) {
   996→      // Deduct from included credits
   997→      await client.query(
   998→        `UPDATE users
   999→         SET music_credits_remaining = music_credits_remaining - 1,
  1000→             updated_at = CURRENT_TIMESTAMP
  1001→         WHERE stripe_customer_id = $1`,
  1002→        [stripeCustomerId]
  1003→      );
  1004→    } else {
  1005→      // Overage - charge flat rate ($1/song)
  1006→      charged = MUSIC_OVERAGE_RATE;
  1007→
  1008→      // Report overage to Stripe meter for billing
  1009→      const usageReport = await reportMusicOverage(stripeCustomerId, 1);
  1010→      if (usageReport.success) {
  1011→        console.log(`[SPLICE] Music overage reported to Stripe: ${stripeCustomerId} - $${charged}`);
  1012→      } else {
  1013→        console.warn(`[SPLICE] Music overage billing failed: ${usageReport.error}`);
  1014→      }
  1015→    }
  1016→
  1017→    // Log the usage
  1018→    await client.query(
  1019→      `INSERT INTO music_usage_log (stripe_customer_id, credits_used, overage_charged)
  1020→       VALUES ($1, 1, $2)
  1021→       ON CONFLICT DO NOTHING`,
  1022→      [stripeCustomerId, charged]
  1023→    );
  1024→
  1025→    await client.query('COMMIT');
  1026→
  1027→    const newCredits = await getMusicCredits(stripeCustomerId);
  1028→    return {
  1029→      success: true,
  1030→      remaining: newCredits.remaining,
  1031→      charged
  1032→    };
  1033→
  1034→  } catch (err) {
  1035→    await client.query('ROLLBACK');
  1036→    console.error('[SPLICE] Music credit deduction error:', err.message);
  1037→    return { success: false, remaining: 0, charged: 0, error: err.message };
  1038→  } finally {
  1039→    client.release();
  1040→  }
  1041→}
