  1207→    );
  1208→
  1209→    if (result.rows.length === 0) {
  1210→      await client.query('ROLLBACK');
  1211→      return { success: false, remaining: 0, charged: 0, error: 'User not found' };
  1212→    }
  1213→
  1214→    const { music_credits_remaining, tier } = result.rows[0];
  1215→    let charged = 0;
  1216→    let creditsDeducted = 0;
  1217→
  1218→    // Determine how many credits to deduct vs overage to charge
  1219→    if (music_credits_remaining >= VARIATIONS_CREDIT_COST) {
  1220→      // Have enough credits - deduct 2.5 (rounded up to 3 for integer credits)
  1221→      creditsDeducted = Math.ceil(VARIATIONS_CREDIT_COST);
  1222→      await client.query(
