  1255→       VALUES ($1, $2, $3)
  1256→       ON CONFLICT DO NOTHING`,
  1257→      [stripeCustomerId, creditsDeducted, charged]
  1258→    );
  1259→
  1260→    await client.query('COMMIT');
  1261→
  1262→    const newCredits = await getMusicCredits(stripeCustomerId);
  1263→    return {
  1264→      success: true,
  1265→      remaining: newCredits.remaining,
  1266→      creditsDeducted,
  1267→      charged
  1268→    };
  1269→
  1270→  } catch (err) {
  1271→    await client.query('ROLLBACK');
