   575→    }
   576→    .repetition-color-1 { background: rgba(255, 107, 107, 0.4); color: #ff6b6b; }
   577→    .repetition-color-2 { background: rgba(78, 205, 196, 0.4); color: #4ecdc4; }
   578→    .repetition-color-3 { background: rgba(255, 230, 109, 0.4); color: #ffe66d; }
   579→    .repetition-color-4 { background: rgba(149, 225, 211, 0.4); color: #95e1d3; }
   580→    .repetition-color-5 { background: rgba(155, 89, 182, 0.4); color: #9b59b6; }
   581→    .repetition-color-6 { background: rgba(241, 196, 15, 0.4); color: #f1c40f; }
   582→    .stutter-word {
