   592→      border-radius: 3px;
   593→      font-size: 9px;
   594→    }
   595→    .color-chip .chip-dot {
   596→      width: 8px;
   597→      height: 8px;
   598→      border-radius: 50%;
   599→    }
   600→
   601→    /* Take Items */
   602→    .takes-list {
   603→      max-height: 200px;
   604→      overflow-y: auto;
   605→    }
   606→    .take-item {
   607→      padding: 8px;
   608→      margin-bottom: 4px;
   609→      background: #2a2a2a;
   610→      border-radius: 4px;
   611→      border-left: 3px solid #555;
