/**
 * SPLICE Plugin Utilities
 *
 * Shared utility functions used across slices.
 */

/**
 * Format seconds as MM:SS
 * @param {number} seconds - Time in seconds
 * @returns {string} Formatted time string (returns '0:00' for invalid input)
 */
function formatTime(seconds) {
  if (typeof seconds !== 'number' || isNaN(seconds)) return '0:00';
  const mins = Math.floor(seconds / 60);
  const secs = Math.floor(seconds % 60);
  return `${mins}:${secs.toString().padStart(2, '0')}`;
}

/**
 * Get active project and sequence
 * @returns {Promise<{project: Object, sequence: Object}|null>}
 */
async function getActiveSequence() {
  const project = await ppro.Project.getActiveProject();
  if (!project) return null;

  const sequence = await project.getActiveSequence();
  if (!sequence) return null;

  return { project, sequence };
}

/**
 * Update status message in UI
 * @param {string} message - Status message to display
 */
function setStatus(message) {
  const statusEl = document.getElementById('status');
  if (statusEl) statusEl.textContent = message;
}

