   913→  const defaultSensitivity = document.getElementById('defaultSensitivity');
   914→  const rememberOptions = document.getElementById('rememberOptions');
   915→
   916→  if (settingsBtn && modal) {
   917→    settingsBtn.addEventListener('click', () => {
   918→      modal.classList.remove('hidden');
   919→      // Sync modal values with current settings
   920→      const settings = getSettings();
   921→      if (defaultSensitivity) defaultSensitivity.value = settings.sensitivity;
   922→      if (rememberOptions) rememberOptions.checked = settings.rememberOptions;
   923→    });
   924→  }
   925→
   926→  if (closeBtn && modal) {
   927→    closeBtn.addEventListener('click', () => {
   928→      modal.classList.add('hidden');
   929→    });
   930→  }
   931→
   932→  // Close on backdrop click
   933→  if (modal) {
   934→    modal.addEventListener('click', (e) => {
   935→      if (e.target === modal) {
   936→        modal.classList.add('hidden');
   937→      }
   938→    });
   939→  }
   940→
   941→  // Save default sensitivity when changed
   942→  if (defaultSensitivity) {
   943→    defaultSensitivity.addEventListener('change', () => {
   944→      saveSettings({ sensitivity: parseInt(defaultSensitivity.value) });
