  1055→/**
  1056→ * Media folder entry (cached after selection)
  1057→ * @type {Object|null}
  1058→ */
  1059→let mediaFolderEntry = null;
  1060→
  1061→/**
  1062→ * Prompt user to select a media folder and create a persistent token.
  1063→ * This allows silent file access without repeated permission prompts.
  1064→ *
  1065→ * @returns {Promise<{success: boolean, path?: string, error?: string}>}
  1066→ */
  1067→async function setupMediaFolder() {
  1068→  try {
  1069→    // Open folder picker dialog
  1070→    const folder = await uxpFs.getFolder();
  1071→
  1072→    if (!folder) {
  1073→      return { success: false, error: 'No folder selected' };
  1074→    }
  1075→
  1076→    // Create a persistent token for this folder
  1077→    const token = await uxpFs.createPersistentToken(folder);
  1078→
  1079→    if (!token) {
  1080→      return { success: false, error: 'Failed to create persistent token' };
  1081→    }
  1082→
  1083→    // Get the folder path for display
  1084→    const folderPath = folder.nativePath || folder.name;
  1085→
  1086→    // Save to settings
  1087→    saveSettings({
  1088→      mediaFolderToken: token,
  1089→      mediaFolderPath: folderPath
  1090→    });
  1091→
  1092→    // Cache the folder entry
  1093→    mediaFolderEntry = folder;
  1094→
  1095→    console.log(`[SPLICE] Media folder set: ${folderPath}`);
  1096→
  1097→    return {
  1098→      success: true,
  1099→      path: folderPath,
  1100→      token
  1101→    };
  1102→  } catch (err) {
  1103→    console.error('[SPLICE] Error setting up media folder:', err);
  1104→    return { success: false, error: err.message };
  1105→  }
  1106→}
  1107→
  1108→/**
  1109→ * Get the media folder entry using the stored persistent token.
  1110→ * Falls back to prompting if token is invalid.
  1111→ *
  1112→ * @returns {Promise<Object|null>} The folder entry or null
  1113→ */
  1114→async function getMediaFolder() {
  1115→  // Return cached entry if available
  1116→  if (mediaFolderEntry) {
  1117→    return mediaFolderEntry;
  1118→  }
  1119→
  1120→  const settings = getSettings();
  1121→
  1122→  // Try to restore from persistent token
  1123→  if (settings.mediaFolderToken) {
  1124→    try {
  1125→      const folder = await uxpFs.getEntryForPersistentToken(settings.mediaFolderToken);
  1126→      if (folder) {
  1127→        mediaFolderEntry = folder;
  1128→        console.log(`[SPLICE] Restored media folder from token: ${settings.mediaFolderPath}`);
  1129→        return folder;
  1130→      }
  1131→    } catch (err) {
  1132→      console.warn('[SPLICE] Could not restore media folder from token:', err.message);
  1133→      // Clear invalid token
  1134→      saveSettings({ mediaFolderToken: null, mediaFolderPath: null });
  1135→    }
  1136→  }
  1137→
  1138→  return null;
  1139→}
  1140→
  1141→/**
  1142→ * Check if a media folder is configured
  1143→ * @returns {boolean}
  1144→ */
  1145→function hasMediaFolder() {
  1146→  const settings = getSettings();
  1147→  return !!settings.mediaFolderToken;
