  1282→        }, FETCH_TIMEOUT_DEFAULT);
  1283→
  1284→        const result = await response.json();
  1285→
  1286→        if (!result.success) {
  1287→          showLoginError(result.error || 'Activation failed');
  1288→          return;
  1289→        }
  1290→
  1291→        // Save customer ID from activation response
  1292→        saveSettings({ customerId: result.customerId });
  1293→
  1294→        // Hide modal
  1295→        loginModal?.classList.add('hidden');
  1296→
  1297→        // Refresh credits display
  1298→        if (typeof refreshCredits === 'function') {
  1299→          await refreshCredits();
