  1502→/**
  1503→ * Parse comma-separated word list into array
  1504→ * @param {string} text - Comma or newline separated words
  1505→ * @returns {Array<string>} Array of trimmed words
  1506→ */
  1507→function parseWordList(text) {
  1508→  if (!text || typeof text !== 'string') return [];
  1509→  return text
  1510→    .split(/[,\n]/)
  1511→    .map(word => word.trim().toLowerCase())
  1512→    .filter(word => word.length > 0);
  1513→}
  1514→
  1515→/**
  1516→ * Format array of words as comma-separated string
  1517→ * @param {Array<string>} words - Array of words
  1518→ * @returns {string} Comma-separated string
  1519→ */
  1520→function formatWordList(words) {
  1521→  if (!Array.isArray(words)) return '';
  1522→  return words.join(', ');
  1523→}
