  1537→  if (!enableCheckbox || !settingsPanel) {
  1538→    console.log('[SPLICE] Profanity UI elements not found');
  1539→    return;
  1540→  }
  1541→
  1542→  // Load saved settings
  1543→  const saved = getProfanitySettings();
  1544→
  1545→  // Apply saved values to UI
  1546→  enableCheckbox.checked = saved.enabled;
  1547→  if (saved.enabled) {
  1548→    settingsPanel.classList.remove('collapsed');
  1549→  }
  1550→
  1551→  if (languageSelect) languageSelect.value = saved.language || 'en';
  1552→  if (blocklistTextarea) blocklistTextarea.value = formatWordList(saved.customBlocklist);
  1553→  if (allowlistTextarea) allowlistTextarea.value = formatWordList(saved.customAllowlist);
  1554→  if (bleepTypeSelect) bleepTypeSelect.value = saved.bleepType || 'standard';
  1555→
  1556→  // Toggle settings panel visibility
  1557→  enableCheckbox.addEventListener('change', () => {
  1558→    if (enableCheckbox.checked) {
  1559→      settingsPanel.classList.remove('collapsed');
  1560→    } else {
  1561→      settingsPanel.classList.add('collapsed');
