  1565→
  1566→  // Save on change
  1567→  if (languageSelect) {
  1568→    languageSelect.addEventListener('change', () => {
  1569→      saveProfanitySettings({ language: languageSelect.value });
  1570→    });
  1571→  }
  1572→
  1573→  if (blocklistTextarea) {
  1574→    blocklistTextarea.addEventListener('blur', () => {
  1575→      const words = parseWordList(blocklistTextarea.value);
  1576→      saveProfanitySettings({ customBlocklist: words });
