   980→        console.error('[SPLICE Builder] Bleep insertion error:', bleepErr);
   981→      }
   982→    }
   983→
   984→    console.log(`[SPLICE Builder] Inserted ${insertedCount}/${bleeps.length} bleeps`);
   985→
   986→    return {
