   387→      multitrackUI.analyzeBtn.textContent = 'Analyze';
   388→    }
   389→  }
   390→}
   391→
   392→// ============================================================================
   393→// AUTO-BALANCE
   394→// ============================================================================
   395→async function autoBalanceMultitrack() {
   396→  if (isMultitrackOperationInProgress) return;
   397→
   398→  // Check online status
   399→  if (typeof isOnline === 'function' && !isOnline()) {
   400→    setStatus('Offline - Check your connection');
   401→    return;
   402→  }
   403→
   404→  isMultitrackOperationInProgress = true;
   405→  if (multitrackUI.autoBalanceBtn) {
   406→    multitrackUI.autoBalanceBtn.disabled = true;
   407→    multitrackUI.autoBalanceBtn.textContent = 'Balancing...';
   408→  }
   409→
   410→  try {
   411→    setStatus('Auto-balancing speaker screentime...');
   412→
   413→    const paths = await getPaths();
   414→    const audioPaths = [paths.wavPath];
   415→
   416→    // Duplicate for multiple speakers (placeholder)
   417→    for (let i = 1; i < speakerConfig.length; i++) {
   418→      audioPaths.push(paths.wavPath);
   419→    }
   420→
   421→    const settings = getMultitrackSettings();
   422→    const apiUrl = getBackendUrl();
   423→
   424→    const response = await fetchWithTimeout(`${apiUrl}/multitrack/auto-balance`, {
   425→      method: 'POST',
   426→      headers: getAuthHeaders(),
   427→      body: JSON.stringify({
   428→        audioPaths,
   429→        speakerNames: settings.speakerNames,
   430→        videoTrackMapping: settings.videoTrackMapping,
   431→        wideShotEnabled: settings.wideShotEnabled,
   432→        wideShotPercentage: settings.wideShotPercentage,
   433→        wideShotTracks: settings.wideShotTracks,
   434→        minShotDuration: settings.minShotDuration,
   435→        switchingFrequency: settings.switchingFrequency
   436→      })
   437→    }, FETCH_TIMEOUT_PROCESSING);
   438→
   439→    if (!response.ok) {
   440→      const errorMsg = await parseErrorResponse(response);
   441→      throw new Error(errorMsg);
   442→    }
   443→
   444→    const data = await response.json();
   445→    analysisResults = data;
   446→
   447→    // Display results
   448→    displayMultitrackResults(data);
   449→
   450→    // Enable apply button
   451→    if (multitrackUI.applyBtn) {
   452→      multitrackUI.applyBtn.disabled = false;
   453→    }
   454→
   455→    const errorInfo = data.finalError ? ` (${data.finalError.toFixed(1)}% deviation)` : '';
   456→    setStatus(`Auto-balance complete${errorInfo}`);
   457→
   458→  } catch (err) {
   459→    console.error('[SPLICE Multitrack] Auto-balance error:', err);
   460→    setStatus('Auto-balance failed: ' + err.message);
   461→  } finally {
   462→    isMultitrackOperationInProgress = false;
   463→    if (multitrackUI.autoBalanceBtn) {
   464→      multitrackUI.autoBalanceBtn.disabled = false;
   465→      multitrackUI.autoBalanceBtn.textContent = 'Auto-Balance';
   466→    }
   467→  }
   468→}
   469→
   470→// ============================================================================
   471→// DISPLAY RESULTS
   472→// ============================================================================
   473→function displayMultitrackResults(results) {
   474→  if (!results) return;
   475→
   476→  // Show preview section
   477→  if (multitrackUI.preview) {
   478→    multitrackUI.preview.classList.remove('hidden');
   479→  }
   480→
   481→  // Render distribution chart
   482→  renderDistributionChart(results.metadata);
   483→
   484→  // Render speaker stats
   485→  renderSpeakerStats(results.metadata);
   486→
   487→  // Render decision list
   488→  renderDecisionList(results.decisions || []);
   489→}
   490→
   491→function renderDistributionChart(metadata) {
   492→  if (!multitrackUI.distributionChart || !metadata) return;
   493→
   494→  const speakerPercentages = metadata.speakerPercentages || {};
   495→  const wideShotPct = metadata.wideShotPercentage || 0;
   496→
   497→  const fragment = document.createDocumentFragment();
   498→
   499→  // Add speaker bars
   500→  let speakerIndex = 0;
   501→  for (const [speaker, pct] of Object.entries(speakerPercentages)) {
   502→    if (pct > 0) {
   503→      const bar = document.createElement('div');
   504→      bar.className = `distribution-bar speaker-${speakerIndex % 4}`;
   505→      bar.style.width = `${pct}%`;
   506→      bar.textContent = pct >= 10 ? `${speaker}: ${pct.toFixed(0)}%` : '';
   507→      bar.title = `${speaker}: ${pct.toFixed(1)}%`;
   508→      fragment.appendChild(bar);
   509→    }
   510→    speakerIndex++;
   511→  }
   512→
   513→  // Add wide shot bar
   514→  if (wideShotPct > 0) {
   515→    const bar = document.createElement('div');
   516→    bar.className = 'distribution-bar wide-shot';
   517→    bar.style.width = `${wideShotPct}%`;
   518→    bar.textContent = wideShotPct >= 8 ? `Wide: ${wideShotPct.toFixed(0)}%` : '';
   519→    bar.title = `Wide Shot: ${wideShotPct.toFixed(1)}%`;
   520→    fragment.appendChild(bar);
   521→  }
   522→
   523→  multitrackUI.distributionChart.innerHTML = '';
   524→  multitrackUI.distributionChart.appendChild(fragment);
   525→}
   526→
   527→function renderSpeakerStats(metadata) {
   528→  if (!multitrackUI.speakerStats || !metadata) return;
   529→
   530→  const speakerPercentages = metadata.speakerPercentages || {};
   531→  const wideShotPct = metadata.wideShotPercentage || 0;
   532→
   533→  const fragment = document.createDocumentFragment();
   534→
   535→  // Add speaker stats
   536→  let speakerIndex = 0;
   537→  for (const [speaker, pct] of Object.entries(speakerPercentages)) {
   538→    const stat = document.createElement('div');
   539→    stat.className = 'speaker-stat';
   540→    stat.innerHTML = `
   541→      <span class="speaker-stat-dot speaker-${speakerIndex % 4}"></span>
   542→      <span>${speaker}: ${pct.toFixed(1)}%</span>
   543→    `;
   544→    fragment.appendChild(stat);
   545→    speakerIndex++;
   546→  }
   547→
   548→  // Add wide shot stat
   549→  if (wideShotPct > 0) {
   550→    const stat = document.createElement('div');
   551→    stat.className = 'speaker-stat';
   552→    stat.innerHTML = `
   553→      <span class="speaker-stat-dot wide-shot"></span>
   554→      <span>Wide: ${wideShotPct.toFixed(1)}%</span>
   555→    `;
   556→    fragment.appendChild(stat);
   557→  }
   558→
   559→  // Add avg shot duration
   560→  if (metadata.averageShotDuration) {
   561→    const stat = document.createElement('div');
   562→    stat.className = 'speaker-stat';
   563→    stat.innerHTML = `<span style="color: #888;">Avg: ${metadata.averageShotDuration.toFixed(1)}s</span>`;
   564→    fragment.appendChild(stat);
   565→  }
   566→
   567→  multitrackUI.speakerStats.innerHTML = '';
   568→  multitrackUI.speakerStats.appendChild(fragment);
   569→}
   570→
   571→function renderDecisionList(decisions) {
   572→  if (!multitrackUI.decisionList) return;
   573→
   574→  // Update count
   575→  if (multitrackUI.decisionCount) {
   576→    multitrackUI.decisionCount.textContent = decisions.length;
   577→  }
   578→
   579→  const fragment = document.createDocumentFragment();
   580→
   581→  // Limit to first 50 for performance
   582→  const displayDecisions = decisions.slice(0, 50);
   583→
   584→  displayDecisions.forEach((decision, i) => {
   585→    const item = document.createElement('div');
   586→    item.className = 'decision-item';
   587→
   588→    const speakerClass = decision.isWideShot ? 'wide-shot' : '';
   589→    const startTime = formatTime(decision.startTime);
   590→    const endTime = formatTime(decision.endTime);
   591→
   592→    item.innerHTML = `
   593→      <span class="decision-time">${startTime}-${endTime}</span>
   594→      <span class="decision-speaker ${speakerClass}">${decision.speakerName}</span>
   595→      <span class="decision-reason">${decision.reason || ''}</span>
   596→      <button class="decision-seek" data-time="${decision.startTime}" title="Seek to ${startTime}" aria-label="Seek to ${startTime}">&gt;</button>
   597→    `;
   598→
   599→    fragment.appendChild(item);
   600→  });
   601→
   602→  if (decisions.length > 50) {
   603→    const more = document.createElement('div');
   604→    more.className = 'decision-item';
   605→    more.style.justifyContent = 'center';
   606→    more.style.color = '#888';
   607→    more.textContent = `+ ${decisions.length - 50} more decisions`;
   608→    fragment.appendChild(more);
   609→  }
   610→
   611→  multitrackUI.decisionList.innerHTML = '';
   612→  multitrackUI.decisionList.appendChild(fragment);
   613→}
   614→
   615→function handleDecisionListClick(e) {
   616→  const seekBtn = e.target.closest('.decision-seek');
   617→  if (seekBtn) {
   618→    e.stopPropagation();
   619→    const time = parseFloat(seekBtn.dataset.time);
   620→    if (!isNaN(time) && typeof seekToTime === 'function') {
   621→      seekToTime(time);
   622→    }
   623→  }
   624→}
   625→
   626→// ============================================================================
   627→// APPLY MULTITRACK CUTS
   628→// ============================================================================
   629→async function applyMultitrackCuts() {
   630→  if (!analysisResults || !analysisResults.decisions) {
   631→    setStatus('No analysis data. Run analysis first.');
   632→    return;
   633→  }
   634→
   635→  if (isMultitrackOperationInProgress) return;
   636→
   637→  isMultitrackOperationInProgress = true;
   638→  if (multitrackUI.applyBtn) {
   639→    multitrackUI.applyBtn.disabled = true;
   640→    multitrackUI.applyBtn.textContent = 'Applying...';
   641→  }
   642→
   643→  try {
   644→    setStatus('Building multitrack sequence...');
   645→
   646→    // Check if builder is available
   647→    if (!window.spliceBuilder) {
   648→      throw new Error('Builder not initialized');
   649→    }
   650→
   651→    // Get source clip info
   652→    const ppro = require('premierepro');
   653→    const project = await ppro.Project.getActiveProject();
   654→    if (!project) throw new Error('No active project');
   655→
   656→    const rootItem = await project.getRootItem();
   657→    const sourceItem = await findFirstMediaItem(rootItem);
   658→    if (!sourceItem) throw new Error('No media items found');
   659→
   660→    // Create cut list from decisions
   661→    const cutList = {
   662→      version: '3.5',
   663→      source: {
   664→        name: sourceItem.name,
   665→        path: sourceItem.treePath
   666→      },
   667→      segments: analysisResults.decisions.map(d => ({
   668→        type: d.isWideShot ? 'wide_shot' : (d.speakerIndex >= 0 ? 'speaker' : 'speech'),
   669→        sourceName: sourceItem.name,
   670→        sourcePath: sourceItem.treePath,
   671→        inPoint: d.startTime,
   672→        outPoint: d.endTime,
   673→        speaker: d.speakerName,
   674→        videoTrack: d.videoTrack,
   675→        colorHint: d.isWideShot ? 'yellow' :
   676→                   (d.speakerIndex === 0 ? 'mango' :
   677→                    d.speakerIndex === 1 ? 'caribbean' :
   678→                    d.speakerIndex === 2 ? 'lavender' : 'cerulean')
   679→      })),
   680→      metadata: {
   681→        isMultitrack: true,
   682→        speakerCount: speakerConfig.length,
   683→        decisionCount: analysisResults.decisions.length,
   684→        ...analysisResults.metadata
   685→      }
   686→    };
   687→
   688→    // Build sequence
   689→    const result = await window.spliceBuilder.buildSequenceFromCutList(cutList, {
   690→      suffix: '_MULTITRACK'
   691→    });
   692→
   693→    if (result.success) {
   694→      // Hide preview
   695→      if (multitrackUI.preview) {
   696→        multitrackUI.preview.classList.add('hidden');
   697→      }
   698→
   699→      setStatus(`Multitrack sequence created: ${result.stats?.clipsInserted || 0} clips`);
   700→
   701→      // Clear results
   702→      analysisResults = null;
   703→    } else {
   704→      throw new Error(result.error || 'Failed to build sequence');
   705→    }
   706→
   707→  } catch (err) {
   708→    console.error('[SPLICE Multitrack] Apply error:', err);
   709→    setStatus('Apply failed: ' + err.message);
   710→  } finally {
   711→    isMultitrackOperationInProgress = false;
   712→    if (multitrackUI.applyBtn) {
   713→      multitrackUI.applyBtn.disabled = true;
   714→      multitrackUI.applyBtn.textContent = 'Apply';
   715→    }
   716→  }
   717→}
   718→
   719→// ============================================================================
   720→// HELPER: Find first media item (duplicated from main.js for module isolation)
   721→// ============================================================================
   722→async function findFirstMediaItem(item) {
   723→  if (!item) return null;
   724→
   725→  const ProjectItemType = {
   726→    CLIP: 1,
   727→    BIN: 2,
   728→    ROOT: 3,
   729→    FILE: 4
   730→  };
   731→
   732→  if (item.type === ProjectItemType.CLIP) {
   733→    return item;
   734→  }
   735→
   736→  if (item.children && item.children.numItems > 0) {
   737→    for (let i = 0; i < item.children.numItems; i++) {
   738→      const child = item.children[i];
   739→      const found = await findFirstMediaItem(child);
   740→      if (found) return found;
   741→    }
   742→  }
   743→
   744→  return null;
   745→}
   746→
   747→// ============================================================================
   748→// EXPORTS
   749→// ============================================================================
   750→// Expose for initialization from main.js
   751→if (typeof window !== 'undefined') {
   752→  window.initMultitrackUI = initMultitrackUI;
   753→  window.spliceMultitrack = {
   754→    init: initMultitrackUI,
   755→    analyze: analyzeMultitrack,
   756→    autoBalance: autoBalanceMultitrack,
   757→    apply: applyMultitrackCuts,
   758→    getSettings: getMultitrackSettings,
   759→    getResults: () => analysisResults
   760→  };
   761→}
