"use client";

import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Play, Zap, Clock, Sparkles, Star, Shield, Users } from "lucide-react";
import { motion } from "framer-motion";
import Link from "next/link";
import Image from "next/image";

export function Hero() {
  return (
    <section className="relative min-h-screen flex items-center justify-center pt-20 pb-16 overflow-hidden dark">
      {/* Background gradient */}
      <div className="absolute inset-0 hero-gradient" />

      {/* Grid pattern */}
      <div className="absolute inset-0 grid-pattern opacity-50" />

      {/* Animated background elements */}
      <div className="absolute inset-0 overflow-hidden">
        <motion.div
          className="absolute top-1/4 left-1/4 w-[500px] h-[500px] bg-[#00D4FF]/10 rounded-full blur-[100px]"
          animate={{
            scale: [1, 1.2, 1],
            opacity: [0.3, 0.5, 0.3],
          }}
          transition={{
            duration: 8,
            repeat: Infinity,
            ease: "easeInOut",
          }}
        />
        <motion.div
          className="absolute bottom-1/4 right-1/4 w-[400px] h-[400px] bg-[#00A8CC]/10 rounded-full blur-[100px]"
          animate={{
            scale: [1.2, 1, 1.2],
            opacity: [0.5, 0.3, 0.5],
          }}
          transition={{
            duration: 8,
            repeat: Infinity,
            ease: "easeInOut",
          }}
        />
      </div>

      <div className="container mx-auto px-4 relative z-10">
        <div className="max-w-4xl mx-auto text-center">
          {/* Social Proof Badge */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            className="flex items-center justify-center gap-4 mb-6"
          >
            <Badge
              variant="outline"
              className="px-4 py-1.5 text-sm font-medium border-[#00D4FF]/50 bg-[#00D4FF]/10 text-[#00D4FF]"
            >
              <Sparkles className="w-3.5 h-3.5 mr-1.5" />
              Professional Beta - Save 40%
            </Badge>
            <div className="hidden sm:flex items-center gap-1 text-sm text-gray-400">
              <div className="flex -space-x-2">
                {[1, 2, 3, 4].map((i) => (
                  <div
                    key={i}
                    className="w-6 h-6 rounded-full bg-gradient-to-br from-[#00D4FF] to-[#0099CC] border-2 border-[#060b14] flex items-center justify-center text-[10px] font-bold text-black"
                  >
                    {String.fromCharCode(64 + i)}
                  </div>
                ))}
              </div>
              <span className="ml-2">
                <span className="text-white font-semibold">10,000+</span> Professional Editors & Studios
              </span>
            </div>
          </motion.div>

          {/* Main Headline */}
          <motion.h1
            className="text-4xl sm:text-5xl md:text-6xl lg:text-7xl font-bold tracking-tight mb-6 text-white"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.1 }}
          >
            Cut Your Editing Time in Half.{" "}
            <span className="gradient-text">Automatically.</span>
          </motion.h1>

          {/* Subheadline - Pain point focused */}
          <motion.p
            className="text-lg sm:text-xl text-gray-400 mb-6 max-w-2xl mx-auto"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.2 }}
          >
            Automate your editing workflow with studio-grade AI.
            Cut silence, isolate vocals, and generate music in seconds—so you can focus on storytelling.
            Native integration for Premiere Pro.
          </motion.p>

          {/* Star Rating */}
          <motion.div
            className="flex items-center justify-center gap-2 mb-8"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.25 }}
          >
            <div className="flex items-center gap-0.5">
              {[1, 2, 3, 4, 5].map((i) => (
                <Star key={i} className="w-5 h-5 fill-yellow-400 text-yellow-400" />
              ))}
            </div>
            <span className="text-sm text-gray-400">
              <span className="text-white font-semibold">4.9/5</span> from verified pros
            </span>
          </motion.div>

          {/* Benefit bullets */}
          <motion.div
            className="flex flex-wrap justify-center gap-6 mb-8"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.3 }}
          >
            <div className="flex items-center gap-2 text-sm text-gray-300">
              <Clock className="w-4 h-4 text-[#00D4FF]" />
              <span>Reduce cut time by 90%</span>
            </div>
            <div className="flex items-center gap-2 text-sm text-gray-300">
              <Zap className="w-4 h-4 text-[#00D4FF]" />
              <span>Native UXP Performance</span>
            </div>
            <div className="flex items-center gap-2 text-sm text-gray-300">
              <Sparkles className="w-4 h-4 text-[#00D4FF]" />
              <span>Studio-Quality Results</span>
            </div>
          </motion.div>

          {/* CTAs */}
          <motion.div
            className="flex flex-col sm:flex-row items-center justify-center gap-4 mb-6"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.4 }}
          >
            <Button
              size="lg"
              className="bg-[#00D4FF] hover:bg-[#00B8E6] text-black font-semibold text-lg px-8 h-14 btn-glow"
              asChild
            >
              <Link href="#pricing">
                Start Free Trial
                <span className="ml-2 text-xs opacity-75">No credit card</span>
              </Link>
            </Button>
            <Button
              size="lg"
              variant="outline"
              className="text-lg px-8 h-14 border-gray-700 text-white hover:bg-white/10"
              asChild
            >
              <Link href="#demo">
                <Play className="w-5 h-5 mr-2" />
                Watch Demo
              </Link>
            </Button>
          </motion.div>

          {/* Trust indicators */}
          <motion.div
            className="flex items-center justify-center gap-6 text-xs text-gray-500"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5, delay: 0.5 }}
          >
            <div className="flex items-center gap-1.5">
              <Shield className="w-4 h-4" />
              <span>14-day free trial</span>
            </div>
            <div className="flex items-center gap-1.5">
              <Users className="w-4 h-4" />
              <span>Cancel anytime</span>
            </div>
            <div className="flex items-center gap-1.5">
              <Zap className="w-4 h-4" />
              <span>Setup in 2 min</span>
            </div>
          </motion.div>

          {/* Video/Demo placeholder */}
          <motion.div
            className="mt-12 relative"
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.7, delay: 0.6 }}
          >
            <div className="relative rounded-xl overflow-hidden shadow-2xl border border-[#00D4FF]/20">
              {/* Video placeholder - replace with actual video */}
              <div className="aspect-video bg-gradient-to-br from-[#0a1929] to-[#0d2137] flex items-center justify-center">
                <div className="text-center">
                  <div
                    className="w-20 h-20 rounded-full bg-[#00D4FF]/20 backdrop-blur flex items-center justify-center mx-auto mb-4 cursor-pointer hover:scale-110 hover:bg-[#00D4FF]/30 transition-all border border-[#00D4FF]/30"
                    id="demo"
                  >
                    <Play className="w-8 h-8 text-[#00D4FF] ml-1" />
                  </div>
                  <p className="text-gray-400 text-sm">Watch 2-minute demo</p>
                </div>
              </div>
              {/* Decorative glow */}
              <div className="absolute inset-0 bg-gradient-to-t from-[#00D4FF]/10 via-transparent to-transparent pointer-events-none" />
            </div>

            {/* Floating UI mockup - Left side */}
            <motion.div
              className="absolute -left-4 top-1/4 sm:left-8 bg-[#0d1829]/90 backdrop-blur rounded-lg p-4 shadow-xl border border-[#00D4FF]/20 hidden md:block"
              animate={{ y: [0, -8, 0] }}
              transition={{ duration: 5, repeat: Infinity, ease: "easeInOut" }}
            >
              <div className="flex items-center gap-3">
                <div className="w-10 h-10 rounded-full bg-green-500/20 flex items-center justify-center">
                  <Clock className="w-5 h-5 text-green-400" />
                </div>
                <div>
                  <p className="font-medium text-sm text-white">Time Saved</p>
                  <p className="text-xs text-green-400">8 hours 23 min</p>
                </div>
              </div>
            </motion.div>

            {/* Floating UI mockup - Right side */}
            <motion.div
              className="absolute -right-4 -bottom-4 sm:right-8 sm:bottom-8 bg-[#0d1829]/90 backdrop-blur rounded-lg p-4 shadow-xl border border-[#00D4FF]/20"
              animate={{ y: [0, -10, 0] }}
              transition={{ duration: 4, repeat: Infinity, ease: "easeInOut" }}
            >
              <div className="flex items-center gap-3">
                <div className="w-10 h-10 rounded-full bg-[#00D4FF]/20 flex items-center justify-center">
                  <Zap className="w-5 h-5 text-[#00D4FF]" />
                </div>
                <div>
                  <p className="font-medium text-sm text-white">Silences Removed</p>
                  <p className="text-xs text-gray-400">47 cuts applied</p>
                </div>
              </div>
            </motion.div>
          </motion.div>


        </div>
      </div>
    </section>
  );
}
