/**
 * Captions Routes
 *
 * Animated captions endpoints
 */

const express = require('express');

/**
 * Create captions routes
 * @param {Object} options - Route configuration options
 * @param {Object} options.middleware - Shared middleware (requireCredits)
 * @returns {express.Router}
 */
function createCaptionsRoutes(options = {}) {
  const router = express.Router();
  const { requireCredits } = options.middleware || {};

  /**
   * POST /animate - Generate animated captions from transcript
   *
   * Creates word-by-word animated captions with template styling.
   */
  router.post('/animate', requireCredits({ endpoint: 'captions' }), async (req, res) => {
    const { transcript, template = 'mrbeast', settings = {} } = req.body;

    if (!transcript) {
      return res.status(400).json({ error: 'transcript is required' });
    }

    try {
      const { generateAnimatedCaptions } = require('../services/animatedCaptions');
      const result = await generateAnimatedCaptions(transcript, template, settings);

      if (!result.success) {
        return res.status(400).json({ error: result.error });
      }

      // Deduct usage
      if (req.deductUsage) {
        await req.deductUsage(0.5); // 0.5 credits per caption generation
      }

      res.json(result);
    } catch (err) {
      console.error('[SPLICE] Animated captions error:', err);
      res.status(500).json({ error: err.message });
    }
  });

  /**
   * GET /templates - Get available caption templates
   */
  router.get('/templates', (req, res) => {
    const { getTemplates } = require('../services/animatedCaptions');
    res.json(getTemplates());
  });

  /**
   * GET /template/:id - Get specific template details
   */
  router.get('/template/:id', (req, res) => {
    const { getTemplateById } = require('../services/animatedCaptions');
    const template = getTemplateById(req.params.id);

    if (!template) {
      return res.status(404).json({ error: `Template '${req.params.id}' not found` });
    }

    res.json(template);
  });

  /**
   * POST /detect-keywords - Detect keywords for highlighting
   */
  router.post('/detect-keywords', requireCredits({ endpoint: 'captions' }), async (req, res) => {
    const { transcript, options: kwOptions = {} } = req.body;

    if (!transcript) {
      return res.status(400).json({ error: 'transcript is required' });
    }

    try {
      const { detectKeywords } = require('../services/animatedCaptions');
      const keywords = await detectKeywords(transcript, kwOptions);

      // Deduct usage
      if (req.deductUsage) {
        await req.deductUsage(0.25); // 0.25 credits
      }

      res.json({ success: true, keywords });
    } catch (err) {
      console.error('[SPLICE] Keyword detection error:', err);
      res.status(500).json({ error: err.message });
    }
  });

  /**
   * POST /insert-emojis - Insert emojis into transcript
   */
  router.post('/insert-emojis', requireCredits({ endpoint: 'captions' }), async (req, res) => {
    const { transcript, options: emojiOptions = {} } = req.body;

    if (!transcript) {
      return res.status(400).json({ error: 'transcript is required' });
    }

    try {
      const { insertEmojis } = require('../services/animatedCaptions');
      const result = await insertEmojis(transcript, emojiOptions);

      if (!result.success) {
        return res.status(400).json({ error: result.error });
      }

      // Deduct usage
      if (req.deductUsage) {
        await req.deductUsage(0.25); // 0.25 credits
      }

      res.json(result);
    } catch (err) {
      console.error('[SPLICE] Emoji insertion error:', err);
      res.status(500).json({ error: err.message });
    }
  });

  /**
   * POST /apply-template - Apply template to existing caption data
   */
  router.post('/apply-template', requireCredits({ endpoint: 'captions' }), (req, res) => {
    const { captionData, templateId } = req.body;

    if (!captionData || !templateId) {
      return res.status(400).json({ error: 'captionData and templateId are required' });
    }

    const { applyTemplate } = require('../services/animatedCaptions');
    const result = applyTemplate(captionData, templateId);

    if (!result.success) {
      return res.status(400).json(result);
    }

    res.json(result);
  });

  /**
   * POST /export/mogrt - Generate MOGRT-compatible data
   */
  router.post('/export/mogrt', requireCredits({ endpoint: 'captions' }), async (req, res) => {
    const { captions, settings = {} } = req.body;

    if (!captions || !Array.isArray(captions)) {
      return res.status(400).json({ error: 'captions array is required' });
    }

    try {
      const { generateMOGRTData } = require('../services/animatedCaptions');
      const result = generateMOGRTData(captions, settings);

      // Deduct usage
      if (req.deductUsage) {
        await req.deductUsage(0.25); // 0.25 credits
      }

      res.json(result);
    } catch (err) {
      console.error('[SPLICE] MOGRT export error:', err);
      res.status(500).json({ error: err.message });
    }
  });

  /**
   * POST /export/burned - Export captions for burning into video
   */
  router.post('/export/burned', requireCredits({ endpoint: 'captions' }), async (req, res) => {
    const { videoPath, captions, outputPath } = req.body;

    if (!captions || !Array.isArray(captions)) {
      return res.status(400).json({ error: 'captions array is required' });
    }

    try {
      const { exportBurnedInCaptions } = require('../services/animatedCaptions');
      const result = await exportBurnedInCaptions(videoPath, captions, outputPath);

      // Deduct usage
      if (req.deductUsage) {
        await req.deductUsage(0.5); // 0.5 credits
      }

      res.json(result);
    } catch (err) {
      console.error('[SPLICE] Burned captions export error:', err);
      res.status(500).json({ error: err.message });
    }
  });

  return router;
}

module.exports = createCaptionsRoutes;

