   568→/**
   569→ * Deduct isolation usage from user's balance
   570→ * Returns overage cost if beyond included hours
   571→ * Uses SELECT...FOR UPDATE to prevent race conditions (like confirmReservation)
   572→ */
   573→async function deductIsolationUsage(stripeCustomerId, audioDurationSeconds, endpoint = 'isolate-vocals') {
   574→  const durationMinutes = audioDurationSeconds / 60;
   575→  const durationHours = audioDurationSeconds / 3600;
   576→
   577→  const client = await pool.connect();
   578→  try {
   579→    await client.query('BEGIN');
   580→
   581→    // Lock the row for this customer - prevents concurrent modifications
   582→    const userResult = await client.query(
   583→      `SELECT id, isolation_hours_remaining, isolation_hours_total, hours_remaining, hours_total, tier
   584→       FROM users WHERE stripe_customer_id = $1
   585→       FOR UPDATE`,
   586→      [stripeCustomerId]
   587→    );
   588→
   589→    if (userResult.rows.length === 0) {
   590→      await client.query('ROLLBACK');
   591→      throw new Error('User not found');
   592→    }
   593→
   594→    const user = userResult.rows[0];
   595→    const remainingIncluded = parseFloat(user.isolation_hours_remaining || 0);
   596→
   597→    let overageCost = 0;
   598→    let hoursFromIncluded = 0;
   599→    let overageMinutes = 0;
   600→
   601→    if (remainingIncluded >= durationHours) {
   602→      // Fully covered by included hours
   603→      hoursFromIncluded = durationHours;
   604→    } else {
   605→      // Partial or full overage
   606→      hoursFromIncluded = remainingIncluded;
   607→      overageMinutes = durationMinutes - (remainingIncluded * 60);
   608→      overageCost = overageMinutes * ISOLATION_OVERAGE_RATE;
   609→    }
   610→
   611→    // Update isolation balance with RETURNING to get new values
   612→    const updateResult = await client.query(
   613→      `UPDATE users
   614→       SET isolation_hours_remaining = GREATEST(0, isolation_hours_remaining - $1),
   615→           updated_at = CURRENT_TIMESTAMP
   616→       WHERE stripe_customer_id = $2
   617→       RETURNING isolation_hours_remaining, isolation_hours_total, hours_remaining, hours_total, tier`,
   618→      [durationHours, stripeCustomerId]
   619→    );
   620→
   621→    const updatedUser = updateResult.rows[0];
   622→
   623→    // Log usage (mark as isolation)
   624→    await client.query(
   625→      `INSERT INTO usage_log (user_id, stripe_customer_id, audio_duration_seconds, hours_used, endpoint)
   626→       VALUES ($1, $2, $3, $4, $5)`,
   627→      [user.id, stripeCustomerId, audioDurationSeconds, durationHours, endpoint]
   628→    );
   629→
   630→    await client.query('COMMIT');
   631→
   632→    // Build balance from RETURNING data (no extra SELECT needed)
   633→    const hoursTotal = parseFloat(updatedUser.hours_total);
   634→    const hoursRemaining = parseFloat(updatedUser.hours_remaining);
   635→    const isolationHoursTotal = parseFloat(updatedUser.isolation_hours_total || 0);
   636→    const isolationHoursRemaining = parseFloat(updatedUser.isolation_hours_remaining || 0);
   637→
   638→    const tierNames = {
   639→      starter: 'Starter',
   640→      pro: 'Pro',
   641→      team: 'Team',
   642→      cancelled: 'Cancelled'
   643→    };
   644→
   645→    return {
   646→      hoursRemaining,
   647→      hoursTotal,
   648→      isolationHoursRemaining,
   649→      isolationHoursTotal,
   650→      tier: updatedUser.tier,
   651→      tierName: tierNames[updatedUser.tier] || updatedUser.tier,
   652→      percentUsed: hoursTotal > 0 ? ((hoursTotal - hoursRemaining) / hoursTotal * 100).toFixed(1) : '0.0',
   653→      isolationPercentUsed: isolationHoursTotal > 0 ? ((isolationHoursTotal - isolationHoursRemaining) / isolationHoursTotal * 100).toFixed(1) : '0.0',
   654→      hasIsolationAccess: TIER_ISOLATION_HOURS[updatedUser.tier] > 0,
   655→      isolationOverageRate: ISOLATION_OVERAGE_RATE,
   656→      isolationUsed: {
   657→        durationMinutes,
   658→        hoursFromIncluded,
   659→        overageMinutes,
   660→        overageCost
   661→      }
   662→    };
   663→  } catch (err) {
   664→    await client.query('ROLLBACK');
   665→    throw err;
   666→  } finally {
   667→    client.release();
   668→  }
   669→}
