       SET hours_remaining = hours_remaining + $1,
           updated_at = CURRENT_TIMESTAMP
       WHERE stripe_customer_id = $2
       RETURNING hours_remaining, hours_total, isolation_hours_remaining, isolation_hours_total, tier`,
