      isolationPercentUsed: isolationHoursTotal > 0 ? ((isolationHoursTotal - isolationHoursRemaining) / isolationHoursTotal * 100).toFixed(1) : '0.0',
      hasIsolationAccess: TIER_ISOLATION_HOURS[user.tier] > 0,
