      [stripeCustomerId, creditsDeducted, charged]
    );

    await client.query('COMMIT');

    const newCredits = await getMusicCredits(stripeCustomerId);
    return {
      success: true,
      remaining: newCredits.remaining,
      creditsDeducted,
      charged
    };

  } catch (err) {
