async function testWithAuth(endpoint, method = 'GET', body = null) {
  try {
    const res = await makeRequest(endpoint, {
      method,
      body,
      headers: {
        'x-stripe-customer-id': 'cus_test123'
      }
    });
    return { status: res.status, data: res.data };
