   247→   247→// Helper to determine tier from price ID with logging
   248→   248→// Supports both monthly and annual pricing
   249→   249→function getTierFromPriceId(priceId) {
   250→   250→  // Monthly prices
   251→   251→  if (priceId === process.env.STRIPE_PRICE_STARTER) return 'starter';
   252→   252→  if (priceId === process.env.STRIPE_PRICE_PRO) return 'pro';
   253→   253→  if (priceId === process.env.STRIPE_PRICE_TEAM) return 'team';
   254→   254→
   255→   255→  // Annual prices (same tier, just different billing period)
   256→   256→  if (priceId === process.env.STRIPE_PRICE_STARTER_ANNUAL) return 'starter';
   257→   257→  if (priceId === process.env.STRIPE_PRICE_PRO_ANNUAL) return 'pro';
   258→   258→  if (priceId === process.env.STRIPE_PRICE_TEAM_ANNUAL) return 'team';
   259→   259→
   260→   260→  // Log unknown price ID for debugging
   261→   261→  console.warn(`[SPLICE] Unknown price ID: ${priceId} - defaulting to starter tier`);
   262→   262→  return 'starter';
   263→   263→}
